import { useState } from "react";

function TicketSearchForm() {
   const [form, setForm] = useState({
      from: "",
      to: "",
      startDate: "",
      endDate: "",
   });

   function handleChange(e) {
      const { name, value } = e.target;
      setForm((prev) => ({ ...prev, [name]: value }));
   }

   function handleSubmit(e) {
      e.preventDefault();
      console.log("Search tickets:", form);
   }

   return (
      <form className="ticket-form" onSubmit={handleSubmit}>
         <div className="ticket-form-row">
            <div className="ticket-field">
               <label htmlFor="from">Пункт отправки</label>
               <input
                  id="from"
                  name="from"
                  type="text"
                  placeholder="Пункт отправки"
                  value={form.from}
                  onChange={handleChange}
               />
            </div>
            <div className="ticket-field">
               <label htmlFor="to">Пункт назначения</label>
               <input
                  id="to"
                  name="to"
                  type="text"
                  placeholder="Пункт назначения"
                  value={form.to}
                  onChange={handleChange}
               />
            </div>
         </div>

         <div className="ticket-form-row">
            <div className="ticket-field">
               <label htmlFor="startDate">Дата начала поездки</label>
               <input
                  id="startDate"
                  name="startDate"
                  type="date"
                  value={form.startDate}
                  onChange={handleChange}
               />
            </div>
            <div className="ticket-field">
               <label htmlFor="endDate">Дата конца поездки</label>
               <input
                  id="endDate"
                  name="endDate"
                  type="date"
                  value={form.endDate}
                  onChange={handleChange}
               />
            </div>
         </div>

         <div className="ticket-form-actions">
            <button type="submit" className="primary-btn">
               Найти билеты
            </button>
         </div>
      </form>
   );
}

export default TicketSearchForm;
